{"command": "node -e "
const fs = require('fs');
const { ethers } = require('ethers');
const abi = JSON.parse(fs.readFileSync('contracts/DaemonToken.json')).abi;
const provider = new ethers.JsonRpcProvider(process.env.BASE_RPC || 'https://mainnet.base.org');
const contract = new ethers.Contract('0xA81e428d5B235C525788529679156039f0D163D4', abi, provider);
Promise.all([
  contract.totalSupply(),
  contract.remainingPublicSupply(),
  contract.remainingDaemonReserve()
]).then(([supply, publicLeft, daemonLeft]) => {
  console.log('Total supply:', ethers.formatEther(supply));
  console.log('Public supply left:', ethers.formatEther(publicLeft));
  console.log('Daemon reserve left:', ethers.formatEther(daemonLeft));
}).catch(e => console.error(e.message));
""}
