  // Check for Transfer events from zero address (mints)
  const filter = contract.filters.Transfer(ethers.ZeroAddress);
  const events = await contract.queryFilter(filter, -1000);
  console.log('Mint events found:', events.length);

  for (const e of events) {
    console.log('Mint:', e.args.to, 'amount:', e.args.value.toString());
  }
}

check().catch(console.error);
